CREATE EXTERNAL TABLE  github_events_hive (rowkey STRING,actor_login STRING,actor_name STRING,repository_id STRING,repository_name STRING,repository_url STRING,repository_watchers INT,repository_stargazers INT,repository_forks INT,repository_language STRING,created_at_year INT,created_at_month INT,created_at_day INT,created_at_weekday INT,created_at_hour INT,created_at_timestamp TIMESTAMP,event_type STRING)
STORED BY 'org.apache.hadoop.hive.hbase.HBaseStorageHandler'
WITH SERDEPROPERTIES ('hbase.columns.mapping' = ':key,actor:actor_login,actor:actor_name,repository:repository_id,repository:repository_name,repository:repository_url,repository:repository_watchers,repository:repository_stargazers,repository:repository_forks,repository:repository_language,created_at:created_at_year,created_at:created_at_month,created_at:created_at_day,created_at:created_at_weekday,created_at:created_at_hour ,created_at:created_at_timestamp,event:event_type') 
TBLPROPERTIES ('hbase.table.name' = 'github_events'); 
